mapper.select:
select a.FACILITY_ID as facilityId , a.LATITUDE as latitude , a.LONGITUDE as longitude , a.DISTANCE as distance , a.FACILITY_ATTR_GRP_ID as attrGrpId , a.ALIAS as attrGrpAlias , a.TYPE as attrGrpType , a.NM as attrGrpNm , case when cd.CD_NM is null then a.val else cd.CD_NM end as attrVal , case when cd.cd_nm is null then null else a.VAL end as attrCdVal from ( select f1.FACILITY_ID , f1.LATITUDE , f1.LONGITUDE , ( 6371 * ACOS( COS(RADIANS(#{p.latitude})) * COS(RADIANS(f1.LATITUDE)) * COS(RADIANS(f1.LONGITUDE) - RADIANS(#{p.longitude})) + SIN(RADIANS(#{p.latitude})) * SIN(RADIANS(f1.LATITUDE)) ) ) * 1000 as DISTANCE , fag.FACILITY_ATTR_GRP_ID , fag.ALIAS , fag.TYPE , fag.CD_GRP_ID , fagl.NM , favl.VAL from m_facility f1 inner join m_facility_attr_val fav on f1.FACILITY_ID = fav.FACILITY_ID and fav.STS inner join m_facility_attr_val_lang favl on fav.FACILITY_ATTR_VAL_ID = favl.FACILITY_ATTR_VAL_ID and favl.STS and favl.LANG_DIV inner join m_facility_attr_grp fag on fav.FACILITY_ATTR_GRP_ID = fag.FACILITY_ATTR_GRP_ID and fag.STS inner join m_facility_attr_grp_lang fagl on fag.FACILITY_ATTR_GRP_ID = fagl.FACILITY_ATTR_GRP_ID and fagl.STS and fagl.LANG_DIV where f1.STS
and f1.REGION_ID = #{p.regionId}
and f1.FACILITY_ID = #{v.facilityId}
and exists ( select * from m_facility f2 inner join m_facility_attr_val fav on f2.FACILITY_ID = fav.FACILITY_ID and fav.STS inner join m_facility_attr_val_lang favl on fav.FACILITY_ATTR_VAL_ID = favl.FACILITY_ATTR_VAL_ID and favl.STS and favl.LANG_DIV inner join m_facility_attr_grp fag on fav.FACILITY_ATTR_GRP_ID = fag.FACILITY_ATTR_GRP_ID and fag.STS where f1.FACILITY_ID = f2.FACILITY_ID and (
or (fag.ALIAS = #{item.attrGrpAlias} and favl.VAL = #{item.val})
) group by f2.FACILITY_ID
having count(*) = #{p.numOfSearchCondition}
)
having DISTANCE <= #{p.radius}
) a left outer join v_cd cd on a.CD_GRP_ID = cd.CD_GRP_ID and a.VAL = cd.CD and cd.LANG_DIV
or a.ALIAS = #{item}
order by facilityId , attrGrpId

mapper.update:
update m_facility
PUBLISH_STS = #{p.publishSts}, LATITUDE = #{p.latitude}, LONGITUDE = #{p.longitude},
where FACILITY_ID = #{p.facilityId}

update m_facility
PUBLISH_STS = #{p.publishSts}, LATITUDE = #{p.latitude}, LONGITUDE = #{p.longitude},
where PARENT_FACILITY_ID = #{v.parentFacilityId}

update m_facility_attr_val_lang favl


  VAL = #{p.val}

where
  exists ( 
    select
        * 
    from
      m_facility_attr_val fav 
    where
      fav.FACILITY_ID = #{v.facilityId} 
      and favl.FACILITY_ATTR_VAL_ID = fav.FACILITY_ATTR_VAL_ID
  )

    and LANG_DIV = #{v.langDiv}
