mapper.select:
select a.FACILITY_ID as facilityId , a.FACILITY_ATTR_GRP_ID as attrGrpId , a.REGION_ID as regionId , a.TYPE as attrGrpType , a.NM as attrGrpNm , case when cd1.CD_NM is null then a.val else cd1.CD_NM end as attrVal , case when cd1.cd_nm is null then null else a.VAL end as attrCdVal from ( select f.FACILITY_ID , f.REGION_ID , fag.FACILITY_ATTR_GRP_ID , fagl.NM , favl.VAL , fag.TYPE , fag.CD_GRP_ID from m_facility f inner join m_facility_attr_val fav on f.FACILITY_ID = fav.FACILITY_ID and fav.STS inner join m_facility_attr_val_lang favl on fav.FACILITY_ATTR_VAL_ID = favl.FACILITY_ATTR_VAL_ID and favl.LANG_DIV and favl.STS inner join m_facility_attr_grp fag on fav.FACILITY_ATTR_GRP_ID = fag.FACILITY_ATTR_GRP_ID and fag.STS
and fag.FACILITY_ATTR_GRP_ID = #{facilityAttrGrpId}
inner join m_facility_attr_grp_lang fagl on fag.FACILITY_ATTR_GRP_ID = fagl.FACILITY_ATTR_GRP_ID and fagl.LANG_DIV and fagl.STS where f.STS
and f.REGION_ID = #{regionId}
and f.FACILITY_ID = #{facilityId}
) a left outer join v_cd cd1 on a.CD_GRP_ID = cd1.CD_GRP_ID and a.VAL = cd1.CD and cd1.LANG_DIV order by a.FACILITY_ID , a.FACILITY_ATTR_GRP_ID
