mapper.select:
select a.REGION_ID as regionId , a.REGION_ATTR_GRP_ID as attrGrpId , b.CD_NM as attrGrpType , a.NM as attrGrpNm , case when c.CD_NM is null then a.val else c.CD_NM end as attrVal , case when c.cd_nm is null then null else a.VAL end as attrCdVal from ( select a.REGION_ID , d.REGION_ATTR_GRP_ID , e.NM , c.VAL , d.TYPE , d.CD_GRP_ID from m_region a inner join m_region_attr_val b on a.REGION_ID = b.REGION_ID and b.STS = 'val' inner join m_region_attr_val_lang c on b.REGION_ATTR_VAL_ID = c.REGION_ATTR_VAL_ID and c.LANG_DIV in ('ja', 'common') and c.STS = 'val' inner join m_region_attr_grp d on b.REGION_ATTR_GRP_ID = d.REGION_ATTR_GRP_ID and d.STS = 'val' inner join m_region_attr_grp_lang e on d.REGION_ATTR_GRP_ID = e.REGION_ATTR_GRP_ID and e.LANG_DIV in ('ja', 'common') and e.STS = 'val' where a.STS = 'val'
and a.REGION_ID = #{id}
) a left outer join v_cd b on b.CD_GRP_ID = '4' and a.TYPE = b.CD and b.LANG_DIV in ('ja', 'common') left outer join v_cd c on a.CD_GRP_ID = c.CD_GRP_ID and a.VAL = c.CD and c.LANG_DIV in ('ja', 'common') order by a.REGION_ID , a.REGION_ATTR_GRP_ID
